ST.xhtml:
//Allows for changing Velocity on the fly. Other then that pretty much the same as the current MC_MoveAbsolute;

IF Execute = TRUE THEN
	IF VelocityOld <> Velocity AND (MC_MoveAbsolute.Busy OR MC_MoveAbsolute2.Busy) THEN
		VelocityChanged:=TRUE;
	END_IF
END_IF
MC_MoveAbsolute(
	Axis:=Axis , 
	Execute:=Execute AND Step=0, 
	Position:=Position,
	Velocity:= Velocity, 
	Acceleration:=Acceleration , 
	Deceleration:=Deceleration , 
	Jerk:=Jerk ,
	);
MC_MoveAbsolute2(
	Axis:=Axis , 
	Execute:=Execute AND Step =1, 
	Velocity:= Velocity, 
	Position:=Position,
	Acceleration:=Acceleration , 
	Deceleration:=Deceleration , 
	Jerk:=Jerk );

CASE Step OF
0:
	IF VelocityChanged THEN
		Step:=1;
		MC_MoveAbsolute(
			Axis:=Axis , 
			Execute:=FALSE);
		MC_MoveAbsolute2(
			Axis:=Axis , 
			Execute:=FALSE);
		VelocityChanged:=FALSE;
	END_IF
	Busy:=MC_MoveAbsolute.Busy;
	Error:=MC_MoveAbsolute.Error;
	ErrorID:=MC_MoveAbsolute.ErrorID;
	CommandAborted:=MC_MoveAbsolute.CommandAborted;
	Done:=MC_MoveAbsolute.Done;
1:
	IF VelocityChanged THEN
		Step:=0;
		MC_MoveAbsolute(
			Axis:=Axis , 
			Execute:=FALSE);
		MC_MoveAbsolute2(
			Axis:=Axis , 
			Execute:=FALSE);
		VelocityChanged:=FALSE;
	END_IF
	Busy:=MC_MoveAbsolute2.Busy;
	Error:=MC_MoveAbsolute2.Error;
	ErrorID:=MC_MoveAbsolute2.ErrorID;
	CommandAborted:=MC_MoveAbsolute2.CommandAborted;
	Done:=MC_MoveAbsolute2.Done;
END_CASE

VelocityOld:=Velocity;

(*	MC_MoveVelocity(
		Axis:=Axis , 
		Execute:=VelocityChanged , 
		Velocity:= Velocity, 
		Acceleration:=Acceleration , 
		Deceleration:=Deceleration , 
		Jerk:=Jerk );*)

InterfaceAsPlainText.xhtml:
FUNCTION_BLOCK FB_MoveAbsoluteAutoUpdateVelocity
VAR_INPUT
	Execute: BOOL;
	Position: LREAL;
	Velocity: LREAL;
	Acceleration: LREAL;
	Deceleration: LREAL;
	Jerk: LREAL;
END_VAR
VAR_OUTPUT
	Busy: BOOL;
	Error: BOOL;
	ErrorID: UDINT;
	CommandAborted: BOOL;
	Done: BOOL;
END_VAR
VAR
	Step: INT;
	MC_MoveAbsolute: MC_MoveAbsolute;
	MC_MoveAbsolute2: MC_MoveAbsolute;
	VelocityChanged: BOOL;
	VelocityOld: LREAL;
	TrigbExecute: R_TRIG;
	TonTimeOut: TON;
END_VAR
VAR_IN_OUT
	Axis: AXIS_REF;
END_VAR
